import google.generativeai as genai
import streamlit as st

key = 'AIzaSyDAyD9PD1_fn0SJ9QN-e2-7OdhebIzbL3w'
genai.configure(api_key=key)

model = genai.GenerativeModel(model_name='gemini-1.5-flash-latest',
                              system_instruction="you are a useful bot")

chat = model.start_chat(history=[])

def chat_gemini(prompt):
    response=chat.send_message(prompt,safety_settings={'HARASSMENT':'block_none'})
    return response.text

st.header("Welcome to Gemini's chatbot")

prompt = st.chat_input('Type what you want to ask Gemini')

with st.sidebar:
    col1,col2,col3=st.columns(3)
    with col2:
        url = 'https://storage.googleapis.com/gweb-uniblog-publish-prod/images/Gemini_SS.width-1300.jpg'
        st.image(url)
    with col1:
        url = 'data:image/png;base64,'
        st.image(url)
    with col3:
        url = 'data:image/jpeg;base64,'
        st.image(url)
    st.audio('03 Legenden.mp3')
    st.video('https://www.youtube.com/watch?v=oSFFe01_Mm0&ab_channel=CogginCollegeofBusiness')

if prompt:
    response = chat_gemini(prompt)
    with st.chat_message("user"):
        st.write(prompt)
    with st.chat_message("assistant"):
        st.write(response)


